-- Sprites
BALL=288
WING=289
TRAIL_0=273
TRAIL_1=274
TRAIL_2=275

-- Consts
H_FLAP=0.1
V_FLAP=1
GRAV=0.02
DAMP=0.5
FRIC=0.9
FLAP_T=5
TRAIL_T=10
TRAIL_V0=50
TRAIL_V1=30
TRAIL_V2=20

t=0

ball={
	x=120,
	y=68,
	dx=0,
	dy=0,
	flp=0,
	d=0,
	fd=0
}

function TableRemove(t,rFunc)
 local j,n=1,#t
 for i=1,n do
  if rFunc(t[i]) then
   if i~=j then t[j]=t[i];t[i]=nil; end
   j=j+1
  else t[i]=nil end
 end
 return t
end

trail={}
function addTrail(x,y)
 table.insert(trail,{x=x,y=y,f=TRAIL_V0})
end
function drawTrail()
 for i,t in ipairs(trail) do
 	spr(t.f<TRAIL_V1 and TRAIL_1 or TRAIL_0,t.x-1,t.y-1,0)
  t.f=t.f-1
 end
 TableRemove(trail,function(v)return v.f>0 end)
end

function load(l)
 local x,y=(l-1)%8,(l-1)//8
 for iy=y,y+30 do
  for ix=x,x+17 do
  	if fget(mget(ix,iy),0) then
    ball.x,ball.y=ix*8+4,iy*8+4
   end
  end
 end
end

load(1)

function TIC()

	if btnp(5) then
	 if ball.dx<0 then
			ball.dx=ball.dx+H_FLAP*2
		else
			ball.dx=ball.dx+H_FLAP
		end
		if ball.g then
			ball.dy=ball.dy-V_FLAP/4
		else
			ball.dy=ball.dy-V_FLAP
		end
		ball.d=0
		ball.fd=FLAP_T
		ball.flp=1
	elseif btnp(4) then
	 if ball.dx>0 then
			ball.dx=ball.dx-H_FLAP*2
		else
			ball.dx=ball.dx-H_FLAP
		end
		if ball.g then
			ball.dy=ball.dy-V_FLAP/4
		else
			ball.dy=ball.dy-V_FLAP
		end
		ball.d=1
		ball.fd=FLAP_T
		ball.flp=1
	end
	
	ball.dy=ball.dy+GRAV
	local yTile=mget(ball.x//8,(ball.y+ball.dy)//8)
	if fget(yTile,2) then
		if fget(yTile,3) then
			ball.dy=ball.dy*-DAMP/4
		--Slanted
		elseif fget(yTile, 4) then
			local lx,ly=ball.x%8,(ball.y+ball.dy)%8
			trace('Locals: ' .. lx .. ',' .. ly .. ',' .. yTile)
			--Down
			if yTile==20 then
				if ly>lx then
					trace('Hit!')
					ball.dy=ball.dy*-DAMP/2
					ball.dx=ball.dx*-DAMP/4
				end
			--Up
			elseif yTile==21 then
				if ly>(8-lx) then
					trace('Hit!')
					ball.dy=ball.dy*-DAMP/2
					ball.dx=ball.dx*-DAMP/4
				end
			end
		else
			trace('hit ground')
			ball.dy=ball.dy*-DAMP
			if fget(yTile,1) then
			end
		end
		if ball.dy<=-0.2 then
			ball.dy=0
			ball.g=true
		end
	else
	 ball.g=false
	end

	local xTile=mget((ball.x+ball.dx)//8,ball.y//8)
	if fget(xTile,2) then
		if fget(xTile,4) then
		else
			ball.dx=ball.dx*-DAMP
			ball.d=ball.d==0 and 1 or 0
		end
	end
	
	if ball.g==true then
	 ball.dx=ball.dx*FRIC
	end
 
 ball.x=ball.x+ball.dx
	ball.y=ball.y+ball.dy
	
	if ball.x<0 then ball.x=240 end
	if ball.x>240 then ball.x=0 end
	if ball.y<0 then ball.y=138 end
	if ball.y>138 then ball.y=0 end

	cls(0)
 map(0,17,30,17,0,0)
 map(0,0,30,17,0,0,0,1,function(tile, x, y)
		if fget(tile, 0) then return 0 end
		return tile
	end)
	drawTrail()
	--spr(BALL,ball.x-4,ball.y-8,0)
	spr(BALL,ball.x-2,ball.y-2,0)
	local s=ball.dy>0 and ball.flp or ball.flp==1 and 0 or 1
	--spr(WING+s,ball.x-(ball.d==0 and 7 or 1),ball.y-10,0,1,ball.d)
	spr(WING+s,ball.x-(ball.d==0 and 5 or 3),ball.y-4,0,1,ball.d)
	if ball.fd>0 then
		ball.fd=ball.fd-1
		if ball.fd<=0 then
		 ball.flp,ball.fd=0,0
		end
	end
	
	t=t+1
	if t%TRAIL_T==0 and math.abs(ball.dy)>0.2 then
	 addTrail(ball.x-1,ball.y-1)
	end
end

