function init()
	p={
		x=120,y=68,
		vx=0,vy=0,
		grounded=false
	}
end

init()
function TIC()
	if btn(2) then
		p.vx=-1
	elseif btn(3) then
		p.vx=1
	else
		p.vx=0
	end
	
	if solid(p.x+p.vx,p.y+p.vy) or
				solid(p.x+7+p.vx,p.y+p.vy) or
				solid(p.x+p.vx,p.y+7+p.vy) or
				solid(p.x+7+p.vx,p.y+7+p.vy) then
		p.vx=0
	end
	
	if solid(p.x,p.y+8+p.vy) or
				solid(p.x+7,p.y+8+p.vy) then
		p.vy=0
		if not p.grounded then
		 p.grounded=true
			sfx(1)
		end
	else
	 p.vy=p.vy+0.2
		p.grounded=false
	end
	
	if p.vy==0 and btn(4) then
		p.vy=-2.5
		sfx(0)
	end
	
	if p.vy<0 and (solid(p.x+p.vx,p.y+p.vy) or
																solid(p.x+7+p.vx,p.y+p.vy)) then
		p.vy=0
		sfx(1)
	end
	
	if p.vx~=0 and p.grounded then
	 sfx(2,12,10,3)
	else
	 sfx(-1,nil,nil,3)
	end
	
	p.x=p.x+p.vx
	p.y=p.y+p.vy
	
	cls()
	map()
	rect(p.x,p.y,8,8,15)
end

local solids={[1]=true,[3]=true}
function solid(x,y)
	return solids[mget(x//8,y//8)]
end

